import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { TransactionServiceService } from '../transaction-service.service';
import { Transactions } from '../Transactions';

@Component({
  selector: 'app-updatetransaction',
  templateUrl: './updatetransaction.component.html',
  styleUrls: ['./updatetransaction.component.css']
})
export class UpdatetransactionComponent {

  id:number
  transaction: Transactions = new Transactions();

    constructor(private transactionservice:TransactionServiceService
      ,private route:ActivatedRoute , private router:Router) { }


    ngOnInit(): void {
      this.id=this.route.snapshot.params['id']
  //this.id=6
  this.transactionservice.getTransactionbyid(this.id).subscribe(data=>{
    this.transactionransaction=data

  },error=>console.log(error));


    }

    onSubmit(){
      console.log(this.Transaction)
      this.transactionservice.updateTransaction(this.Transaction.idTransaction,this.Transaction).subscribe( data =>{console.log(data)
        this.goToInvitationList()

      }
      , error => console.log(error));
    }
    goToInvitationList(){
      this.router.navigate(['/Transaction']);
    }



  }
