import { Component, OnInit } from '@angular/core';
import { Chart } from 'angular-highcharts';
import { ChartDataSets, ChartOptions, ChartType } from 'chart.js';
import { Color, Label } from 'ng2-charts';
import { Transactions } from '../Transactions';

@Component({
  selector: 'app-sales-by-month',
  templateUrl: './sales-by-month.component.html',
  styleUrls: ['./sales-by-month.component.scss']
})

export class SalesByMonthComponent implements OnInit {
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }
  lineChartData: ChartDataSets[] = [
    { data: [85, 72, 78, 75, 77, 75], label: 'Crude oil prices' },
  ];
  lineChartLabels: Label[] = ['January', 'February', 'March', 'April', 'May', 'June'];
  lineChartOptions = {
    responsive: true,
  };
  lineChartColors: Color[] = [
    {
      borderColor: 'black',
      backgroundColor: 'rgba(255,255,0,0.28)',
    },
  ];
  lineChartLegend = true;
  lineChartPlugins = [];
  lineChartType: ChartType = 'line';  


  getTransactionsss() {
    this.lineChartData = [];
    this.lineChartLabels = [];
    this.taskService.getTransactions().subscribe(
      (d) => {
        console.log(d);
        d.forEach((dateAmount: Transactions) => {
          console.log(dateAmount.datetransaction);
          console.log(dateAmount.id);
          if (dateAmount.datetransaction instanceof Date) {
            this.lineChartLabels.push(dateAmount.datetransaction.toLocaleDateString());
          }
        });
      },
      (error) => {
        console.error(error);
      }
    );
  }
}  





  
}
