import { Component, OnInit } from '@angular/core';
import { ChartOptions, ChartType } from 'chart.js';
import { Label } from 'ng2-charts';

@Component({
  selector: 'app-sales-by-month',
  templateUrl: './sales-by-month.component.html',
  styleUrls: ['./sales-by-month.component.scss']
})
export class SalesByMonthComponent implements OnInit {
  public barChartOptions: ChartOptions = {
    responsive: false,
  };

  public barChartLabels: Label[] = [
    'Jan',
    'Feb',
    'Mar',
    'Apr',
    'May',
    'Jun',
    'Jul',
    'Aug',
    'Sep',
    'Oct',
    'Nov',
    'Dec'
  ];

  public barChartType: ChartType = 'line';
  public barChartLegend = true;

  public barChartData = [
    { data: [70, 69, 95, 145, 182, 215, 252, 265, 233, 183, 139, 196], label: 'Arizona' },
    { data: [47, 52, 44, 35, 58, 69, 32, 53, 71, 82, 99, 159], label: 'Connecticut' },
  ];

  constructor() {}

  ngOnInit(): void {}
}
<canvas baseChart
        [data]="barChartData"
        [options]="barChartOptions"
        [plugins]="barChartPlugins"
        [legend]="barChartLegend"
        [type]="'bar'">
</canvas>
