import { Component, OnInit } from '@angular/core';
import { ChartDataSets, ChartOptions } from 'chart.js';
import { Color, Label } from 'ng2-charts';

@Component({
  selector: 'app-sales-by-month',
  templateUrl: './sales-by-month.component.html',
  styleUrls: ['./sales-by-month.component.scss']
})
export class SalesByMonthComponent implements OnInit {
  public lineChartData: any[] = [
    {
      label: 'Bar 1',
      data: [65, 59, 80, 81, 56, 55, 40],
      backgroundColor: '#616161bb',
      borderColor: '#616161',
      hoverBackgroundColor: '#616161',
      borderWidth: 0.5,
    },
    {
      label: 'Bar 2',
      data: [50, 40, 70, 70, 40, 40, 30],
      backgroundColor: '#D2555Cbb',
      borderColor: '#D2555C',
      hoverBackgroundColor: '#D2555C',
      borderWidth: 0.5,
    },
    {
      label: 'Bar 3',
      data: [55, 45, 75, 75, 45, 45, 35],
      backgroundColor: '#33659Cbb',
      borderColor: '#33659C',
      hoverBackgroundColor: '#33659C',
      borderWidth: 0.5,
    },
    {
      label: 'Line 1',
      data: [115, 119, 120, 121, 116, 115, 100],
      type: 'line',
      backgroundColor: '#616161',
      borderColor: '#616161',
      pointBackgroundColor: '#616161',
      pointBorderColor: '#616161',
      pointHoverBorderColor: '#993349',
      pointHoverBackgroundColor: '#993349',
    },
    {
      label: 'Line 2',
      data: [125, 129, 130, 131, 126, 125, 110],
      type: 'line',
      backgroundColor: '#993349',
      borderColor: '#993349',
      pointBackgroundColor: '#993349',
      pointBorderColor: '#993349',
    },
    {
      label: 'Line 3',
      data: [100, 100, 110, 110, 100, 100, 90],
      type: 'line',
      backgroundColor: '#33659C',
      borderColor: '#33659C',
      pointBackgroundColor: '#33659C',
      pointBorderColor: '#33659C',
    },
  ];
  public lineChartLabels: string[] = [
    'January',
    'February',
    'March',
    'April',
    'May',
    'June',
    'July',
  ];
  public lineChartLegend = true;
  public lineChartType = 'line';
  public lineChartPlugins = [];
  public lineChartOptions = {
    tooltip: {
      backgroundColor: '#F6F6F666',
      borderWidth: 2,
      borderColor: '#F6F6F666',
    },
    scales: {
      y: {
        grid: {
          lineWidth: 0,
        },
      },
      x: {
        grid: {
          display: false,
        },
      },
    },
  };}
