import {Component, OnInit, Renderer2} from '@angular/core';
import {AuthService} from "./components/Authentication/auth-service.service";
import {TokenStorageService} from "./components/Authentication/token-storage.service";
import {NavigationEnd, Router} from "@angular/router";
import {filter} from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit{
  title = 'GpgCheckout';
  roles = '';
  isAdmin = false;

  constructor(
    private authService: AuthService,
    private tokenStorage: TokenStorageService,
    private router: Router,
  ) { }

  ngOnInit() {
    this.router.events
      .pipe(filter(event => event instanceof NavigationEnd))
      .subscribe(() => {
        if (this.tokenStorage.getToken()) {
          this.roles = this.tokenStorage.getUser().roles;
          if (this.roles[0] === 'ROLE_ADMIN' || this.roles[0] === 'ROLE_MARCHAND') {
            this.isAdmin = true;
          }
        }
      });
  }
}


<ng-container *ngIf="!isAdmin">
  <!-- Navbar Start -->
  <app-navbar></app-navbar>

  <!-- Navbar End -->

  <router-outlet></router-outlet>

  <!-- Footer Start -->
  <app-footer></app-footer>

  <!-- Footer End -->
  <!-- Copyright Start -->
  <app-copyright></app-copyright>

  <!-- Copyright End -->
</ng-container>
<ng-container *ngIf="isAdmin">
  <router-outlet></router-outlet>
</ng-container>
