import { HttpHeaders } from '@angular/common/http';
import { Component, EventEmitter, Output } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Transactions } from '../Transactions';
import { TransactionServiceService } from '../transaction-service.service';
import { Sites } from '../Sites';
import { SitesService } from '../sites.service';

@Component({
  selector: 'app-addsite',
  templateUrl: './addsite.component.html',
  styleUrls: ['./addsite.component.css']
})
export class AddsiteComponent {
  myForm :FormGroup;
  @Output() aded = new EventEmitter<Sites>();
 site:Sites;
  constructor(private siteserv:SitesService) { }

  ngOnInit(): void {

    this.site = new Sites(); // Initialize the transaction object

    this.myForm=new FormGroup({
      shopName: new FormControl(),
      url: new FormControl(),
      technicianName: new FormControl(),
      description: new FormControl(),
      category: new FormControl(),
      phoneNumberSite: new FormControl(),
      nameTechnician: new FormControl(),
      revenue: new FormControl(),
      activitySector: new FormControl(),
      emailSite: new FormControl(),

    }) 
  }

  onSubmit() {
    // Handle the form submission here
    this.add();
  }

  add() {
    console.log(this.site); // Check the transaction object in the console
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    this.siteserv.addSies(this.site, headers).subscribe(
      response => {
        // Handle the response here
      },
      error => {
        // Handle the error here
      }
    );

    this.aded.emit(this.site);
    this.myForm.reset();
    window.location.reload();
  }

}
<p>addsite works!</p>

<div class="col-md-6 offset-md-3">
  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
        <label>datetransaction</label>
        <input type="date" class="form-control" id="date" [(ngModel)]="transaction.datetransaction" name="datetransaction">
      </div>
      <!-- Add the rest of the fields here -->
      <div class="form-group">
        <label>result</label>
        <input type="text" class="form-control" id="result" [(ngModel)]="transaction.result" name="result">
      </div>
      <div class="form-group">
        <label>surname</label>
        <input type="text" class="form-control" id="surname" [(ngModel)]="transaction.surname" name="surname">
      </div>
      <div class="form-group">
        <label>givenName</label>
        <input type="text" class="form-control" id="givenName" [(ngModel)]="transaction.givenName" name="givenName">
      </div>
      <div class="form-group">
        <label>montant</label>
        <input type="text" class="form-control" id="montant" [(ngModel)]="transaction.montant" name="montant">
      </div>
      <div class="form-group">
        <label>devise</label>
        <input type="text" class="form-control" id="devise" [(ngModel)]="transaction.devise" name="devise">
      </div>

    <button class="btn btn-success" type="submit">Submit</button>
  </form>
</div>
