  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
        <label>datetransaction</label>
        <input type="date" class="form-control" id="date" [(ngModel)]="transaction.datetransaction" name="datetransaction">
      </div>
      <!-- Add the rest of the fields here -->
      <div class="form-group">
        <label>result</label>
        <input type="text" class="form-control" id="result" [(ngModel)]="transaction.result" name="result">
      </div>
      <div class="form-group">
        <label>surname</label>
        <input type="text" class="form-control" id="surname" [(ngModel)]="transaction.surname" name="surname">
      </div>
      <div class="form-group">
        <label>givenName</label>
        <input type="text" class="form-control" id="givenName" [(ngModel)]="transaction.givenName" name="givenName">
      </div>
      <div class="form-group">
        <label>montant</label>
        <input type="text" class="form-control" id="montant" [(ngModel)]="transaction.montant" name="montant">
      </div>
      <div class="form-group">
        <label>devise</label>
        <input type="text" class="form-control" id="devise" [(ngModel)]="transaction.devise" name="devise">
      </div>

    <button class="btn btn-success" type="submit">Submit</button>
  </form>
</div>
