import { Component } from '@angular/core';

@Component({
  selector: 'app-updatesite',
  templateUrl: './updatesite.component.html',
  styleUrls: ['./updatesite.component.css']
})
export class UpdatesiteComponent {
  id: number;
  sites: sites = new Transactions();

  constructor(
    private transactionservice: TransactionServiceService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.transactionservice.getTransactionbyid(this.id).subscribe(
      data => {
        this.Transactions = data;
      },
      error => console.log(error)
    );
  }

  onSubmit() {
    console.log(this.Transactions);
    this.transactionservice.updateTransaction(this.Transactions.idTransaction, this.Transactions).subscribe(
      data => {
        console.log(data);
        this.goToTransactionList();
      },
      error => console.log(error)
    );
  }

  goToTransactionList() {
    this.router.navigate(['transaction']);
  }
}
