import { Users } from '../Users';
import { UserServiceService } from 'src/app/components/Authentication/user-service.service';
import { Router } from '@angular/router';
import { UsersService } from '../users.service';

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent {
  ngOnInit(): void {
    // Add your initialization logic here
  }



  userData:any=[]
  data: any;

  activeindex=-1;

  location='Submit';


  listuser :  Users[]








  showAdd = false;







  constructor(private userser:UsersService,private router:Router) {


  this.userser.getAllUser().subscribe((allData)=>
  {
    console.log(allData)
    this. userData=allData
  })
}



deleteUsers(id:any,i:number){
  this.userser.deleteUsers(id).subscribe((result)=>
  {console.log(result)
   // this.invitattionser.splice(i,1)
    this.userData.splice(i,1)  
    console.log(result)
//this.ngOnInit()
  })
}





