import { HttpHeaders } from '@angular/common/http';
import { Component, EventEmitter, Output } from '@angular/core';
import { FormGroup, FormControl } from '@angular/forms';
import { Users } from '../Users';
import { UserServiceService } from 'src/app/components/Authentication/user-service.service';
import { UsersService } from '../users.service';

@Component({
  selector: 'app-adduser',
  templateUrl: './adduser.component.html',
  styleUrls: ['./adduser.component.css']
})
export class AdduserComponent {
  myForm :FormGroup;
  @Output() aded = new EventEmitter<Users>();
 user:Users;
  constructor(private userserv:UsersService) { }

  ngOnInit(): void {

    this.user = new Users(); // Initialize the transaction object

    this.myForm=new FormGroup({
      username: new FormControl(),
      email: new FormControl(),
      technicianName: new FormControl(),
      description: new FormControl(),
      category: new FormControl(),
      phoneNumberSite: new FormControl(),
      nameTechnician: new FormControl(),
      revenue: new FormControl(),
      activitySector: new FormControl(),
      emailSite: new FormControl(),
      completeAddress: new FormControl(),
    }) 
  }

  onSubmit() {
    // Handle the form submission here
    this.add();
  }

  add() {
    console.log(this.user); // Check the transaction object in the console
    const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

    this.userserv.addUsers(this.user, headers).subscribe(
      response => {
        // Handle the response here
      },
      error => {
        // Handle the error here
      }
    );

    this.aded.emit(this.user);
    this.myForm.reset();
    window.location.reload();
  }

}
<p>adduser works!</p>
<div class="users">
  <div class="col-md-6 offset-md-3">
    <form (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label>username</label>
        <input type="text" class="form-control" id="shopName" [(ngModel)]="user.username" name="shopName">
      </div>
      <div class="form-group">
        <label>URL</label>
        <input type="text" class="form-control" id="url" [(ngModel)]="user.email" name="url">
      </div>
      <div class="form-group">
        <label>Technician Name</label>
        <input type="text" class="form-control" id="technicianName" [(ngModel)]="user.technicianName" name="technicianName">
      </div>
      <div class="form-group">
        <label>Description</label>
        <input type="text" class="form-control" id="description" [(ngModel)]="site.description" name="description">
      </div>
      <div class="form-group">
        <label>Category</label>
        <input type="text" class="form-control" id="category" [(ngModel)]="site.category" name="category">
      </div>
      <div class="form-group">
        <label>Phone Number</label>
        <input type="text" class="form-control" id="phoneNumberSite" [(ngModel)]="site.phoneNumberSite" name="phoneNumberSite">
      </div>
      <div class="form-group">
        <label>Name Technician</label>
        <input type="text" class="form-control" id="nameTechnician" [(ngModel)]="site.nameTechnician" name="nameTechnician">
      </div>
      <div class="form-group">
        <label>Revenue</label>
        <input type="number" class="form-control" id="revenue" [(ngModel)]="site.revenue" name="revenue">
      </div>
      <div class="form-group">
        <label>Activity Sector</label>
        <input type="text" class="form-control" id="activitySector" [(ngModel)]="site.activitySector" name="activitySector">
      </div>
      <div class="form-group">
        <label>Email Site</label>
        <input type="email" class="form-control" id="emailSite" [(ngModel)]="site.emailSite" name="emailSite">
      </div> 
      <div class="form-group">
        <label>completeAddress</label>
        <input type="email" class="form-control" id="completeAddress" [(ngModel)]="site.completeAddress" name="completeAddress">
      </div>

      <button class="btn btn-success" type="submit">Submit</button>
    </form>
  </div>
</div>
