import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { TransactionServiceService } from '../transaction-service.service';
import { Transactions } from '../Transactions';
@Component({
  selector: 'app-updatetransaction',
  templateUrl: './updatetransaction.component.html',
  styleUrls: ['./updatetransaction.component.css']
})
export class UpdatetransactionComponent {
  id: number;
  transaction: Transactions = new IDBTransaction();

  constructor(
    private transactionservice: TransactionServiceService,
    private route: ActivatedRoute,
    private router: Router
  ) { }

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.transactionservice.getTransactions().subscribe(data => {
      this.transaction = data;
    }, error => console.log(error));
  }

  onSubmit() {
    console.log(this.transaction);
    this.transactionservice.updateTransaction(this.transaction.idTransaction, this.transaction).subscribe(data => {
      console.log(data);
      this.goToTransactionList();
    }, error => console.log(error));
  }

  goToTransactionList() {
    this.router.navigate(['/transaction']);
  }
}
