import { Component, OnInit } from '@angular/core';
import { Chart } from 'angular-highcharts';
import { Transactions } from '../Transactions';

@Component({
  selector: 'app-sales-by-month',
  templateUrl: './sales-by-month.component.html',
  styleUrls: ['./sales-by-month.component.scss']
})
export class SalesByMonthComponent implements OnInit {

  chart = new Chart({
    chart: {
      type: 'line',
      height: 325
    },
    title: {
      text: 'Month wise sales'
    },
    xAxis: {
      categories: [
        'Jan',
        'Feb',
        'Mar',
        'Apr',
        'May',
        'Jun',
        'Jul',
        'Aug',
        'Sep',
        'Oct',
        'Nov',
        'Dec'
      ]
    },
    yAxis: {
      title: {
        text: 'Revenue in $'
      }
    },
    series: [
      {
        name: "Arizona",
        type: "line",
        color: '#044342',
        data: [70, 69, 95, 145, 182, 215, 252, 265, 233, 183, 139, 196]
      },
      {
        name: 'Connecticut',
        type: 'line',
        color: '#7e0505',
        data: [
          47, 52, 44, 35, 58, 69, 32, 53, 71, 82, 99, 159
        ]
      },
      {
        name: 'Ohio',
        type: 'line',
        color: '#ed9e20',
        data: [
          [transactions.date, 70]          // Ajoutez les autres valeurs en fonction des dates correspondantes
        ]
      },
    ],
    credits: {
      enabled: false
    }
  })

  constructor(private transactionService: TransactionServiceService) {}

  ngOnInit(): void {
    this.transactionService.getTransactions().subscribe(
      (transactions: Transactions[]) => {

        this.transactions = transactions;
        console.log(transactions)
        this.updateChartData();
      },
      (error) => {
        console.log('Error fetching transactions:', error);
      }
    );
  }

  updateChartData(): void {
    const amounts = this.transactions.map((transaction) => transaction.amount);
    const dates = this.transactions.map((transaction) => transaction.date);
  
    this.doughnutChartData = [{ data: amounts, label: 'Transaction Amount' }];
    this.doughnutChartLabels = dates;
  }
}
