import { Component, OnInit } from '@angular/core';
import { ChartDataSets, ChartOptions, ChartType } from 'chart.js';
import { Color, Label } from 'ng2-charts';
import { Transactions } from '../Transactions';
import { TransactionServiceService } from '../transaction-service.service';

@Component({
  selector: 'app-sales-by-month',
  templateUrl: './sales-by-month.component.html',
  styleUrls: ['./sales-by-month.component.scss']
})

export class SalesByMonthComponent implements OnInit {
  public barChartOptions: ChartOptions = {
    responsive: true,
  };
  public barChartLabels: Label[] = ['Current', 'Proposed'];
  public barChartType: ChartType = 'bar';
  public barChartLegend = true;
  public barChartPlugins = [];
  public colors = [
    { backgroundColor:"red" },
    { backgroundColor:"green" },
    { backgroundColor:"blue" },
    { backgroundColor:"yellow" }
    ];

    public barChartData: ChartDataSets[] = [
      { data: [114400, 171599], label: 'Base', stack: 'a' },
      { data: [100350, 185649], label: 'Variable', stack: 'a' },
      { data: [34320], label: 'KSO', stack: 'a' },
      { data: [74955, 91899], label: 'Upside 130%', stack: 'a' }
    ];

  constructor(private taskService: TransactionServiceService) { }

  ngOnInit(): void {
    this.getTransactions();
  }


  
  getTransactions() {
    
      this.barChartLabels = [];
      this.lineChartData = [];
      this.taskService.getTransactions().subscribe(
        (d) => {
          console.log(d);
          d.forEach((typeCount:Transactions) => {
            console.log(typeCount.datetransaction);
            console.log(typeCount.idTransaction);
            this.barChartLabels.push({ data: [typeCount.idTransaction] });

           // this.lineChartData.push(typeCount.surname);
            this.lineChartLabels.push(typeCount.datetransaction);
          });
        },
        (error) => {
          console.error(error);
        }
      );
    }
  
  }
