import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
@Injectable({
  providedIn: 'root'
})
export class SitesService {
  constructor(private httpClient: HttpClient) { }

  getAllSites(){
    return this.httpClient.get('http://localhost:8089/SpringMVC/api/getallSite/')
  }
  getTransactions(): Observable<Array<Sites>> {
    return this.httpClient

    .get<Array<Transactions>>(`http://localhost:8089/SpringMVC/api/getallTransaction
    `)
    .pipe(map((d: Array<Transactions>) => d));
  }

}
