import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { TransactionServiceService } from '../transaction-service.service';
import { Transactions } from '../Transactions';

@Component({
  selector: 'app-updatetransaction',
  templateUrl: './updatetransaction.component.html',
  styleUrls: ['./updatetransaction.component.css']
})
export class UpdatetransactionComponent {
  id: number;
  Transactions: Transactions = new Transactions();

  constructor(
    private transactionservice: TransactionServiceService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.transactionservice.getTransactionbyid(this.id).subscribe(
      data => {
        this.Transactions = data;
      },
      error => console.log(error)
    );
  }

  onSubmit() {
    console.log(this.Transactions);
    this.transactionservice.updateTransaction(this.Transactions.idTransaction, this.Transactions).subscribe(
      data => {
        console.log(data);
        this.goToTransactionList();
      },
      error => console.log(error)
    );
  }

  goToTransactionList() {
    this.router.navigate(['transaction']);
  }
}
<div class="col-md-6 offset-md-3">
  <h3>Update site</h3>
  <form (ngSubmit)="onSubmit()">
    <div class="form-group">
      <label>shopName</label>
      <input type="text" class="form-control" id="shopName" [(ngModel)]="shopName" name="shopName">
    </div>
    <div class="form-group">
      <label>url</label>
      <input type="text" class="form-control" id="url" [(ngModel)]="url" name="url">
    </div>
    <div class="form-group">
      <label>technicianName</label>
      <input type="text" class="form-control" id="technicianName" [(ngModel)]="technicianName" name="technicianName">
    </div>
    <div class="form-group">
      <label>description</label>
      <input type="text" class="form-control" id="description" [(ngModel)]="description" name="description">
    </div>
    <div class="form-group">
      <label>category</label>
      <input type="text" class="form-control" id="category" [(ngModel)]="category" name="category">
    </div>
    <div class="form-group">
      <label>phoneNumberSite</label>
      <input type="text" class="form-control" id="phoneNumberSite" [(ngModel)]="phoneNumberSite" name="phoneNumberSite">
    </div>
    <div class="form-group">
      <label>nameTechnician</label>
      <input type="text" class="form-control" id="nameTechnician" [(ngModel)]="nameTechnician" name="nameTechnician">
    </div>
    <div class="form-group">
      <label>revenue</label>
      <input type="text" class="form-control" id="revenue" [(ngModel)]="revenue" name="revenue">
    </div>
    <div class="form-group">
      <label>activitySector</label>
      <input type="text" class="form-control" id="activitySector" [(ngModel)]="activitySector" name="activitySector">
    </div>
    <div class="form-group">
      <label>emailSite</label>
      <input type="text" class="form-control" id="emailSite" [(ngModel)]="emailSite" name="emailSite">
    </div>
    <div class="form-group">
      <label>completeAddress</label>
      <input type="text" class="form-control" id="completeAddress" [(ngModel)]="completeAddress" name="completeAddress">
    </div>

    <button class="btn btn-success" type="submit">Submit</button>
  </form>
</div>
