import { Component } from '@angular/core';
import { TransactionServiceService } from '../transaction-service.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-updatetransaction',
  templateUrl: './updatetransaction.component.html',
  styleUrls: ['./updatetransaction.component.css']
})
export class UpdatetransactionComponent {

  id:number
  Transaction: transaction= new transaction();

    constructor(private transactionservice:TransactionServiceService
      ,private route:ActivatedRoute , private router:Router) { }


    ngOnInit(): void {
      this.id=this.route.snapshot.params['id']
  //this.id=6
  this.invitationservice.getInvitationbyid(this.id).subscribe(data=>{
    this.Invitation=data

  },error=>console.log(error));


    }

    onSubmit(){
      console.log(this.Invitation)
      this.invitationservice.updateInvitation(this.Invitation.idInvitation,this.Invitation).subscribe( data =>{console.log(data)
        this.goToInvitationList()

      }
      , error => console.log(error));
    }
    goToInvitationList(){
      this.router.navigate(['/invitation']);
    }



  }
